INSERT INTO `blog_msg` VALUES ('5', '<p style=\"text-align: center;\"><strong><span style=\"font-size: 16px; font-style: italic; font-weight: bold; color: rgb(51, 153, 204); line-height: 18px;\">JavaScript 是属于网络的脚本语言！</span></strong></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-style: italic; font-weight: bold; color: rgb(51, 153, 204); line-height: 18px;\"><strong>JavaScript 被数百万计的网页用来改进设计、验证表单、检测浏览器、创建cookies，以及更多的应用。</strong></span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-style: italic; font-weight: bold; color: rgb(51, 153, 204); line-height: 18px;\"><strong>JavaScript 是因特网上最流行的脚本语言。</strong></span></p><p style=\"text-align: center;\"><span style=\"font-size: 16px; font-style: italic; font-weight: bold; color: rgb(51, 153, 204); line-height: 18px;\"><strong>JavaScript 很容易使用！你一定会喜欢它的！</strong></span></p><p><br/></p>', '1493799062', '一个神奇的世界', null, null);
